const init_state = {
	todoList: [],
	inputTodo: "",
	todoCount: 13,
}

const reducer = (state = init_state, action) => {
	switch (action.type){
		case "INCREMENT_TODO_COUNT":
			return todoCount += 1
			break;
		default:
			return state;
	}
}

export default reducer;
