import logo from './logo.svg';
import './App.css';
import React from "react"
import "bootstrap/dist/css/bootstrap.css"
import "./styles.css"
import TodoItem from "./components/TodoItem"

class App extends React.Component{

  state = {
    todolist : [
        {activity: "Apel", id: "1"},
        {activity: "Mangga", id: "2"},
        {activity: "Pisang", id: "3"}
    ],
  }

  renderTodolist = () => {
    return this.state.todolist.map((val) => {
        return(
            <TodoItem />
        )
    })
  }


  render() {
      return(
      <div>
        <h2>Todo List</h2>
        {this.renderTodolist()}
      </div>
    )
  }


}


export default App;
